function setupMobileMenu(){const e=document.getElementById("mobile-menu-button"),t=document.getElementById("mobile-menu");e&&t&&(e.addEventListener("click",()=>{const n=t.classList.toggle("hidden");e.setAttribute("aria-expanded",String(!n))}),t.querySelectorAll("a").forEach(n=>{n.addEventListener("click",()=>{t.classList.add("hidden"),e.setAttribute("aria-expanded","false")})}))}function setupScrollAnimation(){const e=document.querySelectorAll(".reveal");if(!e.length)return;if(window.matchMedia&&window.matchMedia("(prefers-reduced-motion: reduce)").matches||!("IntersectionObserver"in window))return void e.forEach(e=>e.classList.add("active"));const t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("active"),t.unobserve(e.target))})},{threshold:.1});e.forEach(e=>t.observe(e))}document.addEventListener("DOMContentLoaded",()=>{setupMobileMenu(),setupScrollAnimation()});